mapper.select:
SELECT id
FROM zc_sports_config
WHERE year = #{year}
  AND term = #{term}

SELECT sch_id AS 'schId', is_del AS 'isDel'
FROM zc_sports_stu_success_sch
WHERE id = #{id}
  AND uid = #{uid}
  AND is_del = 0

mapper.update:
UPDATE zc_sports_stu_success_sch
SET is_del = 1
WHERE id = #{id}
  AND uid = #{uid}
  AND is_del = 0

mapper.select:
SELECT FROM_UNIXTIME(preview_start_time) AS 'previewStartTime',
       FROM_UNIXTIME(preview_end_time)   AS 'previewEndTime'
FROM zc_sports_config
WHERE year = #{year}
  AND term = #{term}

SELECT form_id
FROM zc_form_config
WHERE code = #{code}

SELECT id, year, term, campus, course_id AS 'courseId', teacher_uid AS 'teacherId', uid
FROM zc_sports_stu_results
WHERE uid = #{uid}
  AND year = #{year}
  AND term = #{term}

mapper.update:
UPDATE zc_sports_stu_results
SET campus      = #{campus},
    course_id   = #{courseId},
    teacher_uid = #{teacherId},
    update_time = #{updateTime}
WHERE id = #{id}
  AND uid = #{uid}

UPDATE zc_sports_class_list
SET course_num  = #{courseNumber},
    update_time = #{updateTime}
WHERE class_id = #{classId}
  AND year = #{year}
  AND term = #{term}
