mapper.select:
select campus, start_time as 'start', start_time as 'startTime', end_time as 'end', end_time as 'endTime'
from zc_sports_config_selection_time
where is_gregory_date = #{isGregory}
  and config_id = #{configId}

SELECT id
FROM zc_sports_config
WHERE year = #{year}
  AND term = #{term}
